mapper.select:
SELECT b.* FROM
    (
    SELECT ROWNUM rn, a.*
    FROM
        (
        select * from goods


		category = #{category}




		order by ${sort}


		order by goods_name

        ) a
    ) b

	WHERE b.rn > (#{page}-1)*12 AND b.rn <= #{page}*12

select count(*) from goods
category = #{category}
and goods_name like '%'||#{keyword}||'%'

SELECT goods_code, count, goods_name, price, thumb_image, CASE category when '비스킷/크래커' then '비스킷' when '쿠키/파이' then '쿠키' when '유기농/전통과자' then '유기농' when '젤리/캐러멜' then '젤리' when '사탕/껌' then '사탕' when '베이커리/잼' then '베이커리' when '과자/간식세트' then '과자' else category end as category
FROM ( SELECT ROWNUM rn, a.* FROM (
select d.goods_code, count(d.goods_code) count, g.goods_name, g.price, g.thumb_image, g.category from detail d, goods g where d.goods_code = g.goods_code
and category = #{category}
group by d.goods_code, g.goods_name, g.price, g.thumb_image, g.category order by 2 desc
) a ) b WHERE b.rn <= #{num}
